	static public void t_execFromPool(talk t,String s) throws Exception{
		t_execFromPool(t,s,false);
	}

	static public void t_execFromPool(talk t,String s,boolean not_important) throws Exception{
		t_execFromPool(-1,t,s,not_important);
	}

	static public void t_execFromPool(int ver , talk t,String s,boolean not_important) throws Exception{
		boolean bCheckVer = ver != -1;
		StringBuffer sbLog = new StringBuffer();
		try{
			if("oracle".equals(t.f_db_type)){
				s = convertOracleNchar(s);
			}
			t.execFromPool(s);
			if(bCheckVer){
				sbLog.append("[SQL UPDATE SUCCESSFUL] " + s).append("\r\n");
				t.execFromPool("insert into HR_VER_NOTE ( VER , SKEY , SQL_COMMENT , EXEC_RESULT , ERROR_MSG ) select '" + ver + "', isnull(max(SKEY),0) + 1 , '" + convert.ToSql(s) + "' , '1' , '' from HR_VER_NOTE where VER = '" + ver + "'");
			}
		} catch(Exception e){
			if(bCheckVer){
				StringWriter sbWError = new StringWriter();
				e.printStackTrace(new PrintWriter(sbWError));
				t.execFromPool("insert into HR_VER_NOTE ( VER , SKEY , SQL_COMMENT , EXEC_RESULT , ERROR_MSG ) select '" + ver + "', isnull(max(SKEY),0) + 1 , '" + convert.ToSql(s) + "' , '0' , '" + convert.ToSql(sbWError.toString()) + "' from HR_VER_NOTE where VER = '" + ver + "'");

				sbLog.append("\r\n");
				sbLog.append("================================================================================================").append("\r\n");
				sbLog.append("　　資料庫版本 (" + ver + ") 更新異動發生錯誤，請詳讀資料庫版本更新紀錄　　").append("\r\n");
				sbLog.append("　　Database update (" + ver + ") has error occurred. Please read the hrm_version update error log.　　").append("\r\n");
				sbLog.append("------------------------------------------------------------------------------------------------").append("\r\n");
				e.printStackTrace(new PrintWriter(System.err));
				sbLog.append("[SQL UPDATE FAILED] " + s).append("\r\n");
				sbLog.append("[SQL FAILED MESSAGE START]");
				sbLog.append("\r\n");
				sbLog.append(sbWError);
				sbLog.append("\r\n");
				sbLog.append("[SQL FAILED MESSAGE END]").append("\r\n");
				sbLog.append("================================================================================================").append("\r\n");
			}
			if(t.f_db_type.equals("oracle")){
				if(e.toString().indexOf("ORA-01439")!=-1){ // column to be modified must be empty to change datatype
					try{
						if(s.startsWith("alter table ")){
							String table1=s.substring("alter table ".length(),s.indexOf(" ","alter table ".length()+1));
							int aa1=s.indexOf(" modify ")+" modify ".length();
							String column1=s.substring(aa1).trim();
							int aa2=column1.indexOf(" ");
							String type1=column1.substring(aa2+1).trim();
							column1=column1.substring(0,aa2).trim();

							t.execFromPool("alter table "+table1+" add TEMP_"+column1+" "+type1);
							try{
								t.execFromPool("update "+table1+" set TEMP_"+column1+"="+column1);
								t.execFromPool("update "+table1+" set "+column1+"=null");
								t.execFromPool("alter table "+table1+" drop column "+column1);
								t.execFromPool("alter table "+table1+" rename column TEMP_"+column1+" to "+column1);
							} catch(Exception ee){
								t.execFromPool("alter table "+table1+" drop column TEMP_"+column1);
							}
							return;
						}
					} catch(Exception ee){
					}

				}
			}

			if(!bCheckVer){
				System.err.println(e.toString());
				e.printStackTrace(System.out);
			}
			if(not_important) return;
			//ORA-01430:
			if(t.f_db_type.equals("oracle")){
				if(e.toString().indexOf("ORA-01430")!=-1) return;
			} else {
				if(e.toString().indexOf("必須唯一")!=-1) return;
				if(e.toString().indexOf("必須是唯一")!=-1) return;
			}

			throw new Exception("調整資料庫結構發生問題，這是一個不能忽略的狀況，請修正後才能正常執行\r\n"+s+"\r\n"+e.toString());
		}finally{
			if(bCheckVer){
				System.out.println(sbLog.toString());
			}
		}
	}

	static public void drop_key(talk t,String s) throws Exception{
		try{
			String a1[][]=null;
			
			if(t.f_db_type.equals("oracle")){
				a1=t.queryFromPool("select c.constraint_name from user_constraints c,user_cons_columns cc where c.constraint_name = cc.constraint_name and c.table_name ='"+s+"' and c.constraint_type in ('P','U') ");
			} else {
				a1=t.queryFromPool("select b.name from sysobjects a,sysobjects b where a.id=b.parent_obj and a.name ='"+s+"' and b.type='K'");
			}
			for(int a=0;a<a1.length;a++){
				t.execFromPool("alter table "+s+" drop CONSTRAINT "+a1[a][0]);
			}
		} catch(Exception e){
			e.printStackTrace(System.out);
		}
	}

	static public String convertOracleNchar(String sql)throws Exception{
		String nchar = System.getProperty("nchar","2"); 
		if("2".equals(nchar)){
			return sql;
		}
		if(sql.length() == 0){
			return sql;
		}
		sql = sql.trim();
		String sql2 = sql.toLowerCase();
		int pos = sql.indexOf("NVARCHAR");
		if(pos != -1){
			sql = sql.substring(0,pos) + sql.substring(pos + 1);
			if("3".equals(nchar)){
				int pos1 = sql.indexOf("(",pos);
				int pos2 = sql.indexOf(")",pos);
				String temp = sql.substring(pos1 + 1 , pos2).trim();
				int new_length = Integer.parseInt(temp) * 3 / 2;
				if(new_length > 4000){
					new_length = 4000;
				}
				sql =sql.substring(0,pos1 + 1) + new_length + sql.substring(pos2);
			}else if("4".equals(nchar)){
				int pos1 = sql.indexOf("(",pos);
				int pos2 = sql.indexOf(")",pos);
				String temp = sql.substring(pos1 + 1 , pos2).trim();
				int new_length = Integer.parseInt(temp) * 4 / 2;
				if(new_length > 6000){
					new_length = 6000;
				}
				sql =sql.substring(0,pos1 + 1) + new_length + sql.substring(pos2);
			}
		}
		pos = sql.indexOf("nvarchar");
		if(pos != -1){
			if("3".equals(nchar)){
				int pos1 = sql.indexOf("(",pos);
				int pos2 = sql.indexOf(")",pos);
				String temp = sql.substring(pos1 + 1 , pos2).trim();
				int new_length = Integer.parseInt(temp) * 3 / 2;
				if(new_length > 4000){
					new_length = 4000;
				}
				sql =sql.substring(0,pos1 + 1) + new_length + sql.substring(pos2);
			}else if("4".equals(nchar)){
				int pos1 = sql.indexOf("(",pos);
				int pos2 = sql.indexOf(")",pos);
				String temp = sql.substring(pos1 + 1 , pos2).trim();
				int new_length = Integer.parseInt(temp) * 4 / 2;
				if(new_length > 6000){
					new_length = 6000;
				}
				sql =sql.substring(0,pos1 + 1) + new_length + sql.substring(pos2);
			}
		}
		return sql;
	}
