	static public String convertOracleIn(String in1)throws Exception{
		if(in1.startsWith("(") && in1.endsWith(")")){
			Vector vIn1 = cutToken(in1.substring(1,in1.length()-1),",");
			StringBuffer sb1 = new StringBuffer();
			int count = 0;
			sb1.append("(");
			for(int z=0 ; z < vIn1.size() ; z++){
				String iin = (String)vIn1.elementAt(z);
				count++;
				if(count > 1000){
					sb1.append(") or DEPT_NO in (");
					count = 1;
				}else if(z != 0){
					sb1.append(",");
				}
				sb1.append(iin);
			}
			sb1.append(")");
			in1 = sb1.toString();
		}
		return in1;
	}
