	public Vector[] getSplit(String str , Vector[] v){
		String arr1[] = str.split(";");
		for(int i = 0 ; i < arr1.length; i++ ){
			String iStr = arr1[i];
			String arr2[] = iStr.split(",");
			v[0].add(arr2[0]);
			v[1].add(arr2[1]);
		}
		return v;
	}
	
	public Properties getStamp(String path){
		Properties stamp=new Properties();
		try{
			File f1=new File(path);
			if(f1.exists()){
				FileInputStream in=new FileInputStream(f1);
				Reader inStream = new InputStreamReader(in, "UTF-8");
				stamp.load(inStream);
				in.close();
			}
		} catch(Exception e){
		} catch(NoSuchMethodError n){
			System.err.println("--jdk version too low");
		}
		return stamp;
	}

	public Properties getStamp2(String path){
		Properties stamp=new Properties();
		try{
			InputStreamReader isr = new InputStreamReader(new FileInputStream(path), "UTF-8");  
			BufferedReader br = new BufferedReader(isr);  
            String line = null;
            while ((line = br.readLine()) != null) {
				String[] arr = line.split("\t");
				if(arr.length == 2){
					stamp.put(arr[0],arr[1]);
				}
			}
		} catch(Exception e){
		}
		return stamp;
	}

