	/**
	 * 20180608判斷密碼強度
	 * <br><br><b>Author:</b><br>	TIMO
	 * @param String PASS 傳入要檢核之密碼
	 * @param String passWordLevel 傳入CONFIG
	 * @throws
	 * @return String 檢核結果 空白表正常
	**/
	public static String checkPSW(String PASS , String passWordLevel,String filename) throws Exception{
		//HRCONFIG_PASSWORD.LEVELUP
		//四段數字組成並用逗點隔開(Ex. 1,2,3,4 )
		//第一段數字為長度限制(>=)
		//第二段數字為大寫英文碼數限制(>=)
		//第三段數字為小寫英文碼數限制(>=)
		//第四段數字為數字碼數限制(>=)
		//沒有不用檢核
		if(passWordLevel == null || passWordLevel.length() == 0){
			return "";
		}
		String checkLength = "";	//長度
		String checkUPPERCASE = "";	//大寫長度
		String checkLOWERCASE = "";	//小寫長度
		String checkNUMLength = "";	//數字長度
		String arrPSL[] = passWordLevel.split(",");
		//不知道會不會蝦七八亂設 先這樣
		try{checkLength = arrPSL[0];}catch (Exception e){}
		try{checkUPPERCASE = arrPSL[1];}catch (Exception e){}
		try{checkLOWERCASE = arrPSL[2];}catch (Exception e){}
		try{checkNUMLength = arrPSL[3];}catch (Exception e){}

		boolean bCheck = false;
		//長度
		if(checkLength.length() > 0 && Integer.valueOf(checkLength) > PASS.length()){
			bCheck = true;
		}
		//大寫
		String reg = "[A-Z]";
        Pattern pattern = Pattern.compile(reg);
        Matcher matcher = pattern.matcher(PASS);
		int Up = 0;
		while (matcher.find()) {
            Up++;
        }
		if(checkUPPERCASE.length() > 0 && Integer.valueOf(checkUPPERCASE) > Up){
			bCheck = true;
		}
		//小寫
		reg = "[a-z]";
        pattern = Pattern.compile(reg);
        matcher = pattern.matcher(PASS);
		int Low = 0;
		while (matcher.find()) {
            Low++;
        }
		if(checkLOWERCASE.length() > 0 && Integer.valueOf(checkLOWERCASE) > Low){
			bCheck = true;
		}
		//數字
		reg = "[0-9]";
        pattern = Pattern.compile(reg);
        matcher = pattern.matcher(PASS);
		int Num = 0;
		while (matcher.find()) {
            Num++;
        }
		if(checkNUMLength.length() > 0 && Integer.valueOf(checkNUMLength) > Num){
			bCheck = true;
		}
		String errMsg = "";
		if(bCheck){
			errMsg = "請輸入更安全的密碼，密碼強度為：至少" + checkLength + "碼，包含" + checkUPPERCASE + "碼以上大寫英文、" + checkLOWERCASE + "碼以上小寫英文、" + checkNUMLength + "碼以上數字!\\r\\n"
					+ "Please enter a more secure password, the password strength is: at least " + checkLength + " words , including " + checkUPPERCASE + " or more uppercase English " + checkLOWERCASE + " or more lowercase English、" + checkNUMLength + " or more digits!";
		}

		return errMsg;
	}
